def worker(mem=mem):
    def parse_col(col_string=""):
        def get_ncol(letter="AB3"):
            if letter.isdigit():
                return int(letter)
            if letter.isalpha():
                rez = 0
                for pos, char in enumerate(reversed(letter)):
                    rez += (ord(char) - 65) + 26**pos
                return rez
        rez = []
        for x in [get_ncol(x) for x in re.split(r"\W+", col_string.upper())]:
            if x is not None and x not in rez:
                rez.append(x)
        return rez
    
    cols = parse_col(mem.r.key_column)
    sh = self.heap.gd.google_sheets.open_by_key(mem.r.gd_id)
    worksheet = sh.get_worksheet(0)
    allrows = {}
    for col in cols:
        allrows[col] = worksheet.col_values(col)

    rows = {}
    for x in allrows:
        for index, value in enumerate(allrows[x]):
            if index not in rows:
                rows[index] = {}
            rows[index][x] = value
    return rows

try:
    allrows = q2working(worker, "Загрузка таблицы")
