from datetime import datetime
import calendar

def default_row_extender(data, id):
    migration = {
                        "name",
                        "date_of_birth",
                        "address",
                        "email",
                        "phone",
                        "account_owner_name",
                        "account_owner_address",
                        "bank",
                        "iban",
                        "bic",
                        "contract_date1",
                        "contract_date2",
                        }
    months = {"Ja":"01","F":"02","Mä":"03","Ap":"04", "Ma":"05", "Juni":"06", "Juli":"07", "Au":"08", "Se":"09", "Ok":"10", "N":"11", "D":"12"}
    for x in migration:
        data[x] = data.get(const.__getattr__(f"school_{x}"), "")

   
    for month in months:
        cd = data.get("contract_date1", "").upper()
        if cd.startswith(month.upper()):
            data["date1"] = cd[-4:] + "-" + months[month]+ "-01"
        cd = data.get("contract_date2", "").upper()
        if cd.startswith(month.upper()):
            year = int(cd[-4:])
            mon = int(months[month])
            last_date = calendar.monthrange(year, mon)[1]
            data["date2"] = f"{year:04}-{mon:02}-{last_date :02}"
            
    if "iban" in data:
        for index,x in enumerate(data["iban"].replace(" ", "")):
            data[f"{index+1}X"] = x
    if "bic" in data:
        for index,x in enumerate(data["bic"].replace(" ", "")):
            data[f"{index+1}Y"] = x

