def get_std(row={"name":"-"}):
    cu = q2cursor("select * from students where name='{name}' and date_of_birth='{date_of_birth}'".format(**row))
    if cu.row_count() == 1:
        row["std"] = cu.r.std
        return cu.r.std

def checkin(row={"name":"-"}, silent=False):
    std = get_std(row)
    if std is None:  #ученика еще нет, принимаем
        if insert("students", row):
            if not silent:
                q2mess("Ученик <b>{name}</b> дата рождения <b>{date_of_birth}</b> <font color=red>принят!</font><br>Личный номер (код) <b>{std}</b>".format(**row))
        else:
            if not silent:
                q2mess(f"Ошибка при приеме ученика!!!<br><br>{last_error()}")
                return None
    else:
        row["std"] = std
    #обновляем предметы
    q2cursor(f"delete from students_subjects where std = {row['std']}")
    for key, item in row.get("TABLA", {}).items():
        item["std"] = row['std']
        item["price"] = item['rprice']
        item["tmsh"] = 0
        if not insert("students_subjects", item):
            q2mess(f"Ошибка при записи предмета:<br><b>{last_error()}</b>")
    if not silent:
        run_form("_school_students", where = f"std={row['std']}")
    return row['std']

from datetime import datetime
import calendar

def default_row_extender(data, id):
    migration = {
                        "name",
                        "date_of_birth",
                        "address",
                        "email",
                        "phone",
                        "account_owner_name",
                        "account_owner_address",
                        "bank",
                        "iban",
                        "bic",
                        "contract_date1",
                        "contract_date2",
                        }
    months = {"Ja":"01","F":"02","Mä":"03","Ap":"04", "Ma":"05", "Juni":"06", "Juli":"07", "Au":"08", "Se":"09", "Ok":"10", "N":"11", "D":"12"}
    for x in migration:
        data[x] = data.get(const.__getattr__(f"school_{x}"), "")
    try:
        data["date_of_birth"] = datetime.strptime(data["date_of_birth"], "%d.%m.%Y").strftime("%Y-%m-%d")
    except:
        return
    for month in months:
        cd = data.get("contract_date1", "").upper()
        if cd.startswith(month.upper()):
            data["date1"] = cd[-4:] + "-" + months[month]+ "-01"
        # else:
            # data["date1"] = ctod(cd)
        # q2mess(data["date1"])
        cd = data.get("contract_date2", "").upper()
        if cd.startswith(month.upper()):
            year = int_(cd[-4:])
            mon = int_(months[month])
            last_date = calendar.monthrange(year, mon)[1]
            data["date2"] = f"{year:04}-{mon:02}-{last_date :02}"
        # else:
            # data["date2"] = ctod(cd)
            
    if "iban" in data:
        for index,x in enumerate(data["iban"].replace(" ", "")):
            data[f"{index+1}X"] = x
    if "bic" in data:
        for index,x in enumerate(data["bic"].replace(" ", "")):
            data[f"{index+1}Y"] = x
            
    prices = {row["code"]: "☐" for row in q2cursor("select * from subjects").records()}

    for x in prices.keys():
        for y in data.keys():
            if f"#{x}#" in data[y]:
                prices[x] = "☑️"
    price_whr = ", ".join([f'"{key}"' for key in prices.keys() if prices[key] == "☑️" ])
    # q2mess(price_whr )

    sql = f"""
                        select *, 
                            (case when price1<>0 and (
                                select max("*")
                                from  subjects
                                where 
                                    code in ({price_whr }) and discount1= "*"
                                ) ="*" then price1 else price end) as rprice
                        from  subjects
                        where code in  ({price_whr }) 
                                """
    tabla = {}
    cu = q2cursor(sql)
    for row in cu.records():
        row["date1"] = data.get("date1", "")
        row["date2"] = data.get("date2", "")
        if num(row["rprice"]) == 0:
            row["rprice"]=""
        if num(row["lasting"]) == 0:
            row["lasting"]=""
            
        tabla[f"{cu.current_row()+1}"] = row
    data["TABLA"] = tabla
    
    data["TOTAL"] = q2cursor(f"select sum(rprice) as total from ({sql}) qq where price_per_hour == '' ").r.total
    data.update(prices)

def iban_check(iban):
    if iban=="":
        return True
    iban = iban.replace(" ", "")
    return int("".join([x if x in "0123456789" else str(ord(x)-55) for x in iban[4:]+iban[:4]]))%97==1

