def get_std(row={"name":"-"}):
    cu = q2cursor("select * from students where name='{name}' and date_of_birth='{date_of_birth}'".format(**row))
    if cu.row_count() == 1:
        row["std"] = cu.r.std
        return cu.r.std

def checkin(row={"name":"-"}):
    std = get_std(row)
    if std is None:
        insert("students", row)
        q2mess("Ученик <b>{name}</b> <b>{date_of_birth}</b> дата рождения <font color=red>принят!</font><br>Личный номер (код) <b>{std}</b>".format(**row))
    else:
        row["std"] = std
    return row["std"]

