    while get(form.model.get_table_name(), f"code='{code}'", "code") == code:
        code= self.db_data.get_uniq_value(form.model.cursor.table_name, "code", code)

    form.s.code = code

actions.action_worker:
import _dbanken_finder
rows = form.get_grid_selected_rows()
updated = 0
for x in rows:
    row= form.model.get_record(x)
    if bank_extender(row):
        updated = 1
        update(form.model.get_table_name(), row)
if updated:
    form.refresh()
